mapper.update:
UPDATE SET subject = #{subject}, sContent = #{sContent}, sDay = #{sDay}, eDay = #{eDay, jdbcType=VARCHAR},
	sTime = #{sTime, jdbcType=VARCHAR}, eTime = #{eTime, jdbcType=VARCHAR}, color = #{color}, cNum = #{cNum, jdbcType=INTEGER}
FROM schedule
WHERE sNum = #{sNum}

UPDATE SET cName = #{cName}
FROM schCategory
WHERE cNum = #{cNum}

mapper.select:
SELECT cNum, cName, userId
FROM schCategory
WHERE userId = #{userId}
